// post component
import React, { Component } from "react";
import './style.css';
// import PlanitBtn from "../PlanitBtn";
import JoinBtn from "../JoinBtn";
import DeleteBtn from "../DeleteBtn";
import { TripList, ListItem } from "../TripList";
import API from "../../utils/API";

class Post extends Component {
    state = {
        post: [],
        country: "US",
        city: "Miami",
        planned: false,
        people: "Kyle",
        startMonth: "01",
        startDay: "02",
        endMonth: "03",
        endDay: "04"
    }

    componentDidMount() {
        this.loadTrips();
    }

    loadTrips = () => {
        API.getPosts()
            .then(res => {
                console.log(res)
                for (let i=0; i<res.data.length;i++) {
                this.setState({
                post: res.data[i],
                country: res.data[i].country,
                city: res.data[i].city,
                people: res.data[i].people,
                startMonth: res.data[i].startDate.charAt(0)+res.data[i].startDate.charAt(1),
                startDay: res.data[i].startDate.charAt(2)+res.data[i].startDate.charAt(3),
                endMonth: res.data[i].startDate.charAt(4)+res.data[i].startDate.charAt(5),
                endDay: res.data[i].endDay
            })
        }
            console.log(this.state);
        }
            ).catch(err => console.log(err));
    };

    planTrip() {
        return (
            this.setState({ planned: true })
        );
    }
    nextTrip() {
        API.getPosts((req, res) => res.json(res)).then(response => response.json(response)).catch(err => console.log(err));
    }

    render() {
        return (
            <div className="postWrapper">
                <div className="innerWrapper">
                    <div className="postDest" >Destination: {this.state.city}, {this.state.country}</div>
                    <div className="postFriends">Friends: {this.state.people}</div>
                    <div className="postDates">Dates: {this.state.startMonth}/{this.state.startDay} - {this.state.endMonth}/{this.state.endDay}</div>
                    <JoinBtn />
                    {this.state.post.length ? (
                        <TripList>
                            {this.state.post.map(post => (
                                <ListItem key={post.id}>
                                    <div key={post.id}> hi</div>
                                    {/* <div className="tripLocation">{post.city}, {post.country}</div> */}
                                    {/* <div className="tripPeople" key="travelers">Travelers: {post.people}</div>
                                    <div className="dates" key="dates">{post.startMonth}/{post.startDay} - {post.endMonth}/{post.endDay}</div> */}
                                </ListItem>
                ))}
                        </TripList>
                    ) : (
                            <h3>No Results to Display</h3>
                        )}
                    <DeleteBtn onClick={this.nextTrip} />
                </div>
            </div>
        );
    }
}

export default Post;
