// login page
import React, { Component } from "react";
import { BrowserRouter } from "react-router-dom";
import { Redirect } from "react-router";
import SignInBtn from "../components/SigninBtn";
import Footer from "../components/Footer";
// import db from "../../../models/index";
// import User from "../../../models/user";
import API from "../utils/API";

class Login extends Component {
    state = {
        username: "",
        pword: "",
        redirect: false,
    };
    handleInputChange = event => {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]: value
        });
        console.log(this.state);
    }
    handleFormSubmit = async event => {
        event.preventDefault();
        console.log("handleformsubmit fxn");
        await API.getOneUser(this.state.username)
        .then(response=>{
            
            this.setState({redirect: true});
            response.json(response);
        })
        .catch(err=>console.log(err));
        console.log("Login form");
    };
    render() {
        const { redirect } = this.state;
        if (redirect) {
            return <Redirect to="/feed" />;
        }
        return (
            <div>
                <form id="signinForm">
                    <input id="signinUName" name="username" placeholder="Username" onChange={this.handleInputChange} />
                    <input id="signinPword" name="password" placeholder="Password" onChange={this.handleInputChange} />
                    <SignInBtn onClick={this.handleFormSubmit}></SignInBtn>
                </form>
                <Footer />
            </div>
        );
    }
}

export default Login;
