        //     })

            API.getOneUser(this.state.username)
            .then(response=>{
                console.log("Response JSON");
                console.log(response);
                // *******************************************
                // check username bug
                if (response) {
                    console.log("User already exists");
                    alert("This username already exists.");
                    this.setState({
                        username: "",
                        pword: "",
                        redirect: false,
                        cookieUsername: "",
                        cookieID: ""
                    });
                    console.log("State after 1st if "+this.state);
                } else
                {
                    console.log("User does not exist");
                    console.log("Response.data.password = "+response.data.pword);
                    if (response.data.pword !== this.state.pword) {
                        console.log("Incorrect Password");
                        alert("Invalid username or password");
                        this.setState({
                            username: "",
                            pword: "",
                            redirect: false,
                            cookieUsername: "",
                            cookieID: ""
                        });
                        console.log("State after 2nd if "+this.state);
                    } else {
                        console.log("User Creation Success");
                        alert(this.state.username+" has been created!");
                        this.setState({
                            redirect: true,
                            cookieUsername: JSON.parse(atob(Cookies.get("session"))).username,
                            cookieID: JSON.parse(atob(Cookies.get("session"))).id
                        });
                    }
                }
                console.log("response JSON end");
            })
    

            .catch(err => console.log(err));
    };

    render() {
        const { redirect } = this.state;
        if (redirect) {
            return <Redirect to="/signin" />;
        }
        return (
            <div>
                <form id="signupForm">
                    <input id="signupName" type="text" name="name" placeholder="Name" onChange={this.handleInputChange} />
                    <input id="signupUName" type="text" name="username" placeholder="Username" onChange={this.handleInputChange} />
                    <input id="signupPword" type="text" name="password" placeholder="Password" onChange={this.handleInputChange} />
                    <SignUpBtn onClick={this.handleFormSubmit}></SignUpBtn>
                </form>
            </div>
        );
    }
}

export default SignUp;
