// post component
import React, { Component } from "react";
import './style.css';
// import PlanitBtn from "../PlanitBtn";
import JoinBtn from "../JoinBtn";
import DeleteBtn from "../DeleteBtn";
import { TripList, ListItem } from "../TripList";
import API from "../../utils/API";

class Post extends Component {
    state = {
        post: [],
        country: "US",
        city: "Miami",
        planned: false,
        people: "Kyle",
        startMonth: "01",
        startDay: "02",
        startYear: "20",
        endMonth: "03",
        endDay: "04",
        endYear: "20"
    }

    componentDidMount() {
        this.loadPosts();
    }

    loadPosts = () => {
        API.getPosts()
            .then(res => {
                console.log(res)
                this.setState({
                    post: res.data,
                    country: "",
                    city: "",
                    planned: false,
                    people: "",
                    startDate: "",
                    endDate: ""

                });
            }
            ).catch(err => console.log(err));
    };

    planTrip() {
        return (
            this.setState({ planned: true })
        );
    }
    nextTrip() {
        API.getPosts((req, res) => res.json(res)).then(response => response.json(response)).catch(err => console.log(err));
    }
    declinePost = (id) => {
        console.log("decline post fxn");
    };

    render() {
        return (
            <div className="postWrapper">
                <div className="innerWrapper">
                    {!this.state.planned ? (
                        <TripList>
                            {this.state.post.map(post => (
                                <ListItem key={post.id}>
                                    <div className="postDest">{post.city}, {post.country}</div>
                                    <div className="postFriends" key="travelers">Travelers: {post.people}</div>
                                    <div className="postDates" key="dates">{post.startMonth}/{post.startDay}/{post.startYear} - {post.endMonth}/{post.endDay}/{post.endYear}</div>
                                    <JoinBtn />
                                    <DeleteBtn onClick={this.declinePost(post.id)} />
                                </ListItem>

                            ))}
                        </TripList>

                    ) : (
                            <h3>Planned Trip Passed</h3>
                        )}
                </div>
            </div>
        );
    }
}

export default Post;
