//sign up page
import React, {Component} from "react";
import SignUpBtn from "../components/SignupBtn";
import TopInterests from "../components/TopInterests";
import OtherInterests from "../components/OtherInterests";
import db from "../../../models/index";
import User from "../../../models/user";

class SignUp extends Component {
    state = {
        username: "",
        password: ""
    };

    handleInputChange = event => {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]: value
        });
    }

    handleFormSubmit = event => {
        event.preventDefault();
        // this.searchGiphy(this.state.search);
        // function checking if username exists already - if does prompts for new entry, if not creates new user
        db.Users.findOne({username:this.state.username}).then(name=>{
            if(name === 0) {
                db.Users.create({username: this.state.username, password: this.state.password});
                console.log("Username: "+this.state.username+" Password: "+this.state.password);
            } else {
                this.setState({username:"", password:""});
                alert("This username is already taken. Please choose a different one.");
                console.log("Username: "+this.state.username+" Password: "+this.state.password);
            }
        });
        console.log("New user created");
    };

    render() {
    return (
        <div>
            <form id="signupForm">
                <input id="signupUName" type="text" name="username" placeholder="Username" />
                <input id="signupPword" type="text" name="password" placeholder="Password" />
                <TopInterests />
                <TopInterests />
                <TopInterests />
                <OtherInterests />
                <SignUpBtn />
            </form>
        </div>
    );
    }
}

export default SignUp;
